DROP TABLE hotel;
CREATE TABLE hotel (id int(10) unsigned NOT NULL AUTO_INCREMENT, address text, street varchar(255), suburb varchar(255), city varchar(255), state varchar(255), postcode varchar(255), country varchar(255), time_zone varchar(255), address_component_json text, chain varchar(255), circles decimal(19,2), date_created datetime, description text, emails_json text, google_name varchar(255), google_place_id varchar(255), heading varchar(255), images_json text, last_updated datetime, last_updated_address datetime, latitude double, longitude double, name varchar(255), number_of_rooms int, phone_number varchar(255), stars decimal(19,2), tag varchar(255), uuid varchar(255), website varchar(255), last_rating_id int(10) unsigned, shop_rating bit NOT NULL, shop_range_short int, shop_range_long int, shop_max_length_of_stay int, PRIMARY KEY (id), INDEX hotel_uuid_idx (uuid), INDEX hotel_website_idx (website), INDEX hotel_scrape_selector_idx (time_zone, shop_range_short, shop_range_long), INDEX hotel_stars_idx (stars), INDEX hotel_circles_idx (circles), INDEX hotel_shop_range_short (shop_range_short), INDEX hotel_shop_range_long (shop_range_long), INDEX hotel_shop_rating (shop_rating), INDEX hotel_last_updated_address (last_updated_address), INDEX hotel_latitude_longitude_idx (latitude, longitude), FULLTEXT INDEX name (name, address)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE hotel_translation;
CREATE TABLE hotel_translation (id bigint NOT NULL AUTO_INCREMENT, name varchar(255) COLLATE utf8_unicode_ci, description text COLLATE utf8_unicode_ci, hotel_id bigint NOT NULL, language varchar(255) COLLATE utf8_unicode_ci NOT NULL, heading varchar(255), PRIMARY KEY (id)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
